import { useState } from "react";


function Form (){

    const  [searchStateValue, setSearchStateValue] = useState('');
    const [searchCityValue, setSearchCityValue] = useState('');

   const handleFunction = (e) =>{
        e.preventDefault();
       e.target.name === 'city' ? setSearchCityValue(e.target.value): setSearchStateValue(e.target.value)


    //    const {name, value} = e.target
    //     setSearchValue({
    //         [name]: value
    //     })
    }


   const submitFunction = (e) =>{
        e.preventDefault();

    }



    return(
        <div>
            <form>
            <input 
                name = "state" 
                placeholder = "State"
                onChange = {handleFunction}
                value = {searchStateValue}>
                </input> 
                <input 
                name = "city" 
                placeholder = "City"
                onChange = {handleFunction}
                value = {searchCityValue}>
                </input>

                <button onClick = {submitFunction}>Search!</button>
                <h1>{searchCityValue}</h1>
                <h1>{searchStateValue}</h1>
            </form>
        </div>
    )
}

export default Form;
